import React, { useCallback, FormEvent, useState } from "react";

import { useDispatch, useSelector } from "../../../config/store";
import { navigate } from "../../Services/action";
import { login, changeEmail, changePassword } from "../logic/action";
import { setLayoutOptions } from "../../../layout/Layout";

const Login = () => {
  const dispatch = useDispatch();

  const [showLoader, setLoader] = useState(false);

  const email = useSelector((state) => state.auth.email);
  const password = useSelector((state) => state.auth.password);

  const onEmailChange = useCallback((e: FormEvent<HTMLInputElement>) => {
    dispatch(changeEmail(e.currentTarget.value));
  }, []);

  const onPasswordChange = useCallback((e: FormEvent<HTMLInputElement>) => {
    dispatch(changePassword(e.currentTarget.value));
  }, []);

  const onLoginClick = useCallback(() => {
    setLoader(true);
    dispatch(login());
  }, []);

  const onNavigateRegister = useCallback(() => {
    dispatch(navigate("Register"));
  }, []);

  const tableHeads = ["Ad", "Soyad", "Telefon"];
  const users = [
    {
      id: 1,
      name: "metin",
      surname: "kara",
      phone: "12345",
    },
    {
      id: 2,
      name: "hülya",
      surname: "kara",
      phone: "544231",
    },
    {
      id: 3,
      name: "elif",
      surname: "kara",
      phone: "12312345",
    },
  ];

  const renderRow = (data: any) => {
    console.log("data: ", data);
    return (
      <>
        {data.map((item: any) => {
          return (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.surname}</td>
              <td>{item.phone}</td>
            </tr>
          );
        })}
      </>
    );
    // return <div>test</div>
  };

  const renderTable = () => {
    return (
      <table>
        <thead>{tableHeads.map(item => item)}</thead>
        <tbody>{renderRow(users)}</tbody>
      </table>
    );
  };

  return (
    <form className="login-wrapper">
      <h2>Candidates Pages</h2>

      <div>{renderTable()}</div>

      {showLoader && (
        <div className="row">
          <div className="col-md-12">
            <div className="loader"></div>
          </div>
        </div>
      )}
    </form>
  );
};

setLayoutOptions(Login, {
  layout: "MainLayout",
});
export default Login;
