import React, { useEffect } from "react";
import { setLayoutOptions } from "../../../layout/Layout";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "../../../config/store";
import { getCandidateDetails } from "../logic/action";

const CandidateDetail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  console.log("sonunda çalıştıııı", id);

  const candidateDetails = useSelector(state => state.candidates.candidateDetails);

  useEffect(() => {
    dispatch(getCandidateDetails(id || ""));
  }, [id]);

  const tableHeads = ["Ad", "Soyad", "Telefon", "Detay"];
  const renderRow = (data: any) => {
    // console.log("data: ", data);
    return (
      <>
        {data &&
          data.length &&
          data.map((item: any) => {
            return (
              <tr key={item.id}>
                <td>{item.name}</td>
                <td>{item.username}</td>
                <td>{item.phone}</td>
                <button
                  type="button"
                  onClick={() => navigate("CandidateDetail", ...[`${item.id}`])}
                >
                  Detay
                </button>
              </tr>
            );
          })}
      </>
    );
    // return <div>test</div>
  };

  const renderTable = (data: any) => {
    return (
      <table>
        <thead>
          <tr>
            {tableHeads.map((item) => (
              <th>{item}</th>
            ))}
          </tr>
        </thead>
        <tbody>{renderRow(data)}</tbody>
      </table>
    );
  };

  return (
    <form className="" style={{ margin: 100 }}>
      <h2>Candidate Details</h2>

      <div>{renderTable(candidates)}</div>
    </form>
  );
};

setLayoutOptions(CandidateDetail, {
  layout: "MainLayout",
});
export default CandidateDetail;
