import React, { useCallback, FormEvent, useState, useEffect } from "react";

import { useDispatch, useSelector } from "../../../config/store";
import { navigate } from "../../Services/action";
// import { login, changeEmail, changePassword } from "../logic/action";
import { setLayoutOptions } from "../../../layout/Layout";
import { getCandidates } from "../logic/action";

const Candidates = () => {
  const dispatch = useDispatch();

  const [showLoader, setLoader] = useState(false);

  const candidates = useSelector(state => state.candidates.candidates);
    console.log('candidates: ', candidates);
//   const onEmailChange = useCallback((e: FormEvent<HTMLInputElement>) => {
//     dispatch(changeEmail(e.currentTarget.value));
//   }, []);

//   const onPasswordChange = useCallback((e: FormEvent<HTMLInputElement>) => {
//     dispatch(changePassword(e.currentTarget.value));
//   }, []);

//   const onLoginClick = useCallback(() => {
//     setLoader(true);
//     dispatch(login());
//   }, []);

    useEffect(() => {
        console.log('çalıoştıı');
        dispatch(getCandidates());
    }, []);

  const onNavigateRegister = useCallback(() => {
    dispatch(navigate("Register"));
  }, []);

  const tableHeads = ["Ad", "Soyad", "Telefon"];
  const users = [
    {
      id: 1,
      name: "metin",
      surname: "kara",
      phone: "12345",
    },
    {
      id: 2,
      name: "hülya",
      surname: "kara",
      phone: "544231",
    },
    {
      id: 3,
      name: "elif",
      surname: "kara",
      phone: "12312345",
    },
  ];

  const renderRow = (data: any) => {
    // console.log("data: ", data);
    return (
      <>
        {data.map((item: any) => {
          return (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.surname}</td>
              <td>{item.phone}</td>
            </tr>
          );
        })}
      </>
    );
    // return <div>test</div>
  };

  const renderTable = () => {
    return (
      <table>
        <thead>
          <tr>
            {tableHeads.map((item) => (
              <th>{item}</th>
            ))}
          </tr>
        </thead>
        <tbody>{renderRow(users)}</tbody>
      </table>
    );
  };

  return (
    <form className="" style={{margin: 100}}>
      <h2>Candidates</h2>

      <div>{renderTable()}</div>

      {showLoader && (
        <div className="row">
          <div className="col-md-12">
            <div className="loader"></div>
          </div>
        </div>
      )}
    </form>
  );
};

setLayoutOptions(Candidates, {
  layout: "MainLayout",
});
export default Candidates;
