import React, { useMemo } from 'react';
import { Route, Router as BrowserRouter, Switch } from 'react-router-dom';
import { mapIn } from 'redux-immutable-helper';
import { createBrowserHistory } from 'history';
import { ModalContainer, ModalRoute } from 'react-router-modal';

import '../config/styles.css';

import Layout from '../layout/Layout';
import Login from '../pages/Auth/views/Login';
import Register from '../pages/Auth/views/Register';
import Home from '../pages/Home/views/Home';
import NotFound from '../layout/components/NotFound';
import Cart from '../pages/Cart/views/Cart';
import Checkout from '../pages/Cart/views/Checkout';
import Category from '../pages/Category/views/Category';
import Product from '../pages/Product/views/Product';

export const history = createBrowserHistory();

export type RouteName =
	| 'Home'
	| 'Login'
	| 'Register'
	| 'Cart'
	| 'Checkout'
	| 'Product'
	| 'Category';

interface IRoute {
	path: string;
	params?: string;
	component: any;
	exact?: boolean;
	isModal?: boolean;
	parentPath?: string;
}

const _routes = {
	// Auth
	Login: {
		path: '/account/login',
		component: Login,
		exact: true,
	},
	Register: {
		path: '/account/Register',
		component: Register,
		exact: true
	},

	// Home
	Home: {
		path: '/',
		component: Home,
		exact: true
	},

	// Cart
	Cart: {
		path: '/cart',
		component: Cart,
		exact: true
	},
	Checkout: {
		path: '/checkout',
		component: Checkout,
		exact: true
	},

	// Category
	Category: {
		path: '/category',
		component: Category,
		exact: true
	},

	// Product
	Product: {
		path: '/product',
		component: Product,
		exact: true
	}
};

export const routes: { [key: string]: IRoute } = _routes;

export default () => {
	const onNavigateParentPath = (parentPath: string | undefined) => () => {
		if (!parentPath)
			return;

		history.replace(parentPath);
	};

	const renderPages = useMemo(
		() =>
			Object.values(routes)
				.filter(value => !value.isModal)
				.map(({ path, params, component, exact }, index) => (
					<Route
						key={index}
						path={path.concat(params || '')}
						component={() => (
							<Layout
								layoutOptions={component.layoutOptions || {}}
								Component={component}
							/>
						)}
						exact={exact}
					/>
				)),
		[],
	);

	const renderModals = useMemo(
		() =>
			Object.values(routes)
				.filter(value => value.isModal)
				.map(({ path, parentPath, params, component, exact }, index) => (
					<ModalRoute
						key={index}
						path={path.concat(params || '')}
						parentPath={parentPath}
						component={() => (
							<Layout
								layoutOptions={component.layoutOptions || {}}
								Component={component}
							/>
						)}
						exact={exact}
						history={history}
						className='react-router-custom-modal'
						inClassName='react-router-custom-modal-in'
						outClassName='react-router-custom-modal-out'
						backdropClassName='react-router-custom-backdrop'
						backdropInClassName='react-router-custom-backdrop-in'
						backdropOutClassName='react-router-custom-backdrop-out'
						onBackdropClick={onNavigateParentPath(parentPath)}
						outDelay={300}
					/>
				)),
		[],
	);

	return (
		<BrowserRouter history={history}>
			<Switch>
				{renderPages}
				<Route path='/*' component={NotFound} />
			</Switch>
			{renderModals}
			<ModalContainer />
		</BrowserRouter>
	);
};


